var fs = require('fs');

module.exports = function(req, res, context){
  var path = context.themeDir + "/" + context.resource, 
    mtype = 'image/' + context.ext.substring(1);
  fs.readFile(path, function (err, data) {
    if (err) throw err;
    res.send(data, { 'Content-Type': mtype }, 200);
  });
}